// grab the Voicetics factory
var Voicetics = require('./lib/voicetics-node');

// create an instance of the mixpanel client
var voicetics = Voicetics.init('962dbca1bbc54701d402c94d65b4a20e');
voicetics.set_config({ debug: true });

// track an event with optional properties
voicetics.track("intent", {
    intentName: "some unique client id",
    slots: "{h:123;b:123}",
    sessionId: "812738123",
    userId: "912389123"
});
